<?php if(!defined('BASEPATH')) exit('No direct script access allowed');class Welcome extends CI_Controller {    public function __construct(){        parent::__construct();        $this->load->model('acceso_model','mod');    }	public function index()	{	 $this->data['error'] = $this->session->flashdata('error');		$this->load->view('login',$this->data);	}	public function login(){		if($_POST['username']!="" && $_POST['password']!=""){            $data = array(                'username' =>$this->input->post("username"),                'password' =>$this->input->post("password"));            $return = $this->mod->login($data);            if(count($return)==0){                $this->session->set_flashdata('error', "El usuario o contrasña no son validos");                redirect('/welcome', 'location');            }else{                $this->session->set_userdata('user',$return);                redirect('/home', 'location');            }		}else{            $this->session->set_flashdata('error', "No puede tener  usuario o Contraseña Vacios");            redirect('/welcome', 'location');        }	}            public function salir(){              $this->session->unset_userdata('user');		        redirect('/welcome', 'location');	  }		}